import React, { useEffect, useState } from 'react';
import "./App.css";



const App = () => {
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
  const [loginDirty, setLoginDirty] = useState(false)
  const [passwordDirty, setPasswordDirty] = useState(false)
  const [loginError, setLoginError] = useState('Логин не может быть пустым')
  const [passwordError, setPasswordError] = useState('Пароль не может быть пустым')

  const loginHandler = (e: any) => {
    setLogin(e.target.value)
    const re = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    return re.test(String(login).toLowerCase());
    if(!re.test(String(e.target.value).toLowerCase())) {
      setLoginError('Некоректно')
    } else {
      setLoginError('')
    }
  }

  const blurHeader = (e: any) => {
    switch (e.target.name) {
      case 'login':
        setLoginDirty(true)
        break
      case 'password':
        setPasswordDirty(true)
        break
    }
  }



  return (
    <div className="app">
      <form>
        <h1>Регистрация</h1>

        {(loginDirty && loginError) && <div style={{color:'red'}}>{loginError}</div>}
        <input value={login} onBlur={e => blurHeader(e)} name='login' type="text" placeholder="Enter eour login..."/>

        {(passwordDirty && passwordError) && <div style={{color:'red'}}>{passwordError}</div>}
        <input value={password} onBlur={e => blurHeader(e)} name='password' type="password" placeholder="Enter eour password..."/>

        <button type='submit'>Registration</button>
      </form>
    </div>
  );
};

export default App;
