import React, { FC, useEffect, useState } from "react";
import { Mod } from "../models/Mod";
import { Modes } from "../models/Modes";
import BoardComponent from "./BoardComponent";
import { Board } from "../models/Board";

interface DebutProps {
    title: string;
    debuts: string[];
    children?: string;
    mod: Mod;
}


const DebutsMake: FC<DebutProps> = ({title, debuts, children, mod}) => {

    const [login, setLogin] = useState('')
    const [loginDirty, setLoginDirty] = useState(false)
    const [loginError, setLoginError] = useState('Логин не может быть пустым')

    const loginHandler = (e: any) => {
        setLogin(e.target.value)
        if(e.target.value.length < 3 || e.target.value.length > 20) {
          setLoginError('Название дебюта должно быть от 3 до 20 символов')
          if(!e.target.value) {
            setLoginError('Название дебюта не может быть пустым')
          }
        } else {
          setLoginError('')
        }
      }

      const blurHeader = (e: any) => {
        switch (e.target.name) {
          case 'login':
            setLoginDirty(true)
            break
        }
      }


    return (
        <p className = "debuts">
            <h3>{title}</h3>
            {debuts.map(debut => 
                <p key={1}>
                    <button>{debut}</button>
                </p>
            )}
            <input onChange={e => loginHandler(e)} value={login} onBlur={e => blurHeader(e)} name='login' type="text" placeholder="Enter eour login..."/>
        </p>
    );
};

export default DebutsMake;
