import React, { FC, useEffect } from "react";
import { Mod } from "../models/Mod";
import { Modes } from "../models/Modes";
import BoardComponent from "./BoardComponent";

interface DebutProps {
    title: string;
    debuts: string[];
    children?: string;
    mod: Mod;
}


const DebutsMake: FC<DebutProps> = ({title, debuts, children, mod}) => {


    const clickUp = (mod: Mod) => {
        mod.mod = Modes.CLICK
    }

    useEffect(() => { // чтобы реакт перерисовал компонент
        clickUp(mod) // вызывается на любое изменение ячейки
    }, [BoardComponent])

    return (
        <p className = "debuts">
            <h3>{title}</h3>
            {debuts.map(debut => 
                <p key={1}>
                    <button>{debut}</button>
                </p>
            )}
            <button onClick={() => clickUp(mod)}>{children}</button>
        </p>
    );
};

export default DebutsMake;
