import React, { FC, useEffect, useState } from "react";
import { Board } from "../models/Board";

interface DebutProps {
    title: string;
    debuts: string[];
    board: Board;
    restart: () => void;
}


const DebutsMake: FC<DebutProps> = ({title, debuts, board, restart}) => {

    const [login, setLogin] = useState('')
    const [loginDirty, setLoginDirty] = useState(false)
    const [loginError, setLoginError] = useState('Логин не может быть пустым')
    const [formValid, setFormValid] = useState(false)

  useEffect(() => {
    if (loginError) {
      setFormValid(false)
    } else {
      setFormValid(true)
    }
  }, [loginError])

    const loginHandler = (e: any) => {
        setLogin(e.target.value)
        if(e.target.value.length < 3 || e.target.value.length > 20) {
          setLoginError('Название дебюта должно быть от 3 до 20 символов')
          if(!e.target.value) {
            setLoginError('Название дебюта не может быть пустым')
          }
        } else {
          setLoginError('')
        }
      }

      const blurHeader = (e: any) => {
        switch (e.target.name) {
          case 'Debut':
            setLoginDirty(true)
            break
        }
      }

      const entrance = () => {
        //отправить название на бэк и потом рестарт
        board.Debuts.push(login)

        restart()
      }



    return (
        <p className = "debuts">
            <h3>{title}</h3>
            {debuts.map(debut => 
                <p key={1}>
                    <button>{debut}</button>
                </p>
            )}
            <input onChange={e => loginHandler(e)} id="textInput" value={login} onBlur={e => blurHeader(e)} name='debut' type="text" placeholder="Enter your debut..."/>
            <button onClick={() => entrance()} disabled={!formValid} type='submit'>+</button>
        </p>
    );
};

export default DebutsMake;
