import React, { useEffect, useState } from 'react';
import "./App.css";
import axios from 'axios';
import { useMutation } from 'react-query';

const API_URL = 'http://localhost:8080'



const App = () => {
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
  const [loginDirty, setLoginDirty] = useState(false)
  const [passwordDirty, setPasswordDirty] = useState(false)
  const [loginError, setLoginError] = useState('Логин не может быть пустым')
  const [passwordError, setPasswordError] = useState('Пароль не может быть пустым')
  const [formValid, setFormValid] = useState(false)

  useEffect(() => {
    if (loginError || passwordError) {
      setFormValid(false)
    } else {
      setFormValid(true)
    }
  }, [loginError, passwordError])

  const loginHandler = (e: any) => {
    setLogin(e.target.value)
    if(e.target.value.length < 3 || e.target.value.length > 20) {
      setLoginError('Логин должен быть от 3 до 20 символов')
      if(!e.target.value) {
        setPasswordError('Логин не может быть пустым')
      }
    } else {
      setLoginError('')
    }
  }

  const passwordHandler = (e: any) => {
    setPassword(e.target.value)
    if (e.target.value.length < 3 || e.target.value.length > 8) {
      setPasswordError('Пароль должен быть от 3 до 8 символов')
      if(!e.target.value) {
        setPasswordError('Пароль не может быть пустым')
      }
    } else {
      setPasswordError('')
    }
  }

  const blurHeader = (e: any) => {
    switch (e.target.name) {
      case 'login':
        setLoginDirty(true)
        break
      case 'password':
        setPasswordDirty(true)
        break
    }
  }

  const [user, setUser] = useState(false)
  const {mutate, isLoading} = useMutation(
    'login',
    () =>
      axios.post(
        `${API_URL}/auth/login`,
        {login, password},
        {
          headers: {'Content-Type':
            'application/json'},
        }
      ),
    {
      onSuccess: ({data}) => {
        setUser(data.user) 
      },
    }
  )



  const registrations = () => {

    setUser(true)
  }


  return (
    <div className="loagin">
      {!user ? (
        <form>
        <h1>Регистрация</h1>

        {(loginDirty && loginError) && <div style={{color:'red'}}>{loginError}</div>}
        <input onChange={e => loginHandler(e)} value={login} onBlur={e => blurHeader(e)} name='login' type="text" placeholder="Enter eour login..."/>

        {(passwordDirty && passwordError) && <div style={{color:'red'}}>{passwordError}</div>}
        <input onChange={e => passwordHandler(e)} value={password} onBlur={e => blurHeader(e)} name='password' type="password" placeholder="Enter eour password..."/>

        <button onClick={() => mutate()} disabled={!formValid} type='submit'>Регистрация</button>

        <button disabled={!formValid} type='submit'>Вход</button>
      </form>

      ) : (
        <h1>T</h1>
      )}



    </div>
  );
};

export default App;
