import React, { FC } from "react";
import { Mod } from "../models/Mod";
import { Modes } from "../models/Modes";

interface DebutProps {
    debut: string;
    restart: () => void;
    mod: Mod;
}


const DebutsPlay: FC<DebutProps> = ({debut, restart, mod}) => {

    const makes = (debut: string) => {
        mod.mod = Modes.PLAY;
        mod.debut = debut;
        //console.log({debut})
        restart()
      }

      const playWhite = () => {
        mod.mod = Modes.PLAYWHITE;
        restart()
      }

      const playBlack = () => {
        mod.mod = Modes.PLAYBLACK;
        restart()
      }

      if((mod.mod === Modes.PLAY) && (mod.debut === debut)) {
        return (
            <p>
                <button onClick={() => makes(debut)}>{debut}</button>
                <p></p>
                <button onClick={() => playWhite()}>Белыми</button>
                <button onClick={() => playBlack()}>Черными</button>
            </p>
        );
      }

    return (
        <p>
            <button onClick={() => makes(debut)}>{debut}</button>
        </p>
    );
};


export default DebutsPlay;
